import { Add, PlayArrow, ThumbDownAltOutlined, ThumbUpAltOutlined } from "@material-ui/icons"
import { useState } from "react"
import "./listcreate.scss"
import { useEffect } from "react"
import axios from "axios"
import { Link } from "react-router-dom"

export default function Listcreate({index5 , item}) {

  const [isHovered, setIsHovered] = useState(false);
  const [movie, setMovie] = useState({});
  
  useEffect(() => {
   const getMovie = async ()=>{
    try {
      const res = await axios.get("/movies/find/"+ item , {   
        headers:{
          token: "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyYTQzYzhlNGQ0ZDNhOGRlNjNjY2M2NyIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY1NTM1MTU2MywiZXhwIjoxNjU1NzgzNTYzfQ.KQXZGUjGtxurCBoeyL3FR8R6aIP49PjguOXF8iG7law"
        },
      });

     setMovie(res.data)

    } catch (err) {
      console.log(err);
    }
   }
   getMovie();
  },[item]);
  return  (
   <Link to={{pathname:"/watch" , movir:movie }}>
    <div className='listitem' 
    style={{left : isHovered && index5 * 290 - 20 +  index5 * 2.5 }}
    onMouseEnter={()=>setIsHovered(true)}
    onMouseLeave={()=>setIsHovered(false)}>
      <img src={movie.img} alt="" />
      {isHovered && (
       <>
        <video src={movie.trailer} autoPlay={true} loop ></video>
        <div className="itemInfo">
        <div className="icons">
         <PlayArrow  className="icon"/>
         <Add className="icon"/>
         <ThumbUpAltOutlined className="icon"/>
         <ThumbDownAltOutlined className="icon"/>
        </div>

        <div className="itemInfoTop">
         <span>{movie.duration}</span>
         <span className="limit">+{movie.limit}</span>
         <span>{movie.year}</span>
        </div>
         <div className="desc"> {movie.desc} </div>
         <div className="genre">{movie.genre}</div>
      </div>
     </>
       )}     
    </div>
  </Link>
  )
}
