import { Add, PlayArrow, ThumbDownAltOutlined, ThumbUpAltOutlined } from "@material-ui/icons"
import { useState } from "react"
import "./listcreate.scss"
import netflixtra from "../../assets/netflixtra.mp4"
import { useEffect } from "react"
import axios from "axios"

export default function Listcreate({index5 , item}) {

  const [isHovered, setIsHovered] = useState(false)
  
  useEffect(() => {
   const getMovie = async ()=>{
    try {
      const res = await axios.get("/moives/find/"+ item , {   
        headers:{
          token: "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyYTQzYzhlNGQ0ZDNhOGRlNjNjY2M2NyIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY1NTM1MTU2MywiZXhwIjoxNjU1NzgzNTYzfQ.KQXZGUjGtxurCBoeyL3FR8R6aIP49PjguOXF8iG7law"
        },
      });
    } catch (err) {
      console.log(err);
    }
   }
  },[])
  return (
    <div className='listitem' 
    style={{left : isHovered && index5 * 290 - 20 +  index5 * 2.5 }}
    onMouseEnter={()=>setIsHovered(true)}
    onMouseLeave={()=>setIsHovered(false)}>
      <img src={item.img} alt="" />
      {
        isHovered && (

       <>
      <video src={item.trailer} autoPlay={true} loop ></video>
     <div className="itemInfo">
       <div className="icons">
         <PlayArrow  className="icon"/>
         <Add className="icon"/>
         <ThumbUpAltOutlined className="icon"/>
         <ThumbDownAltOutlined className="icon"/>
       </div>

       <div className="itemInfoTop">
         <span>{item.duration}</span>
         <span className="limit">+{item.limit}</span>
         <span>{item.year}</span>
       </div>
       <div className="desc"> {item.desc} </div>
       <div className="genre">{item.genre}</div>
     </div>
     </>
       )}     
    </div>
  )
}
