import { ArrowBackIosOutlined, ArrowForwardIosOutlined } from "@material-ui/icons"
import "./list.scss"
import Listcreate from "../listcreate/Listcreate"
import { useRef, useState } from "react"

 function List() {


  const [ isMoved , setisMoved] = useState(false)
  const [ slideNumber, setSlideNumber ] = useState(0)

  const listRef = useRef();

  const handleClick = (direction) => {
    setisMoved(true)
    let distance = listRef.current.getBoundingClientRect().x -52
    if(direction === "left" && slideNumber > 0 ){ 
       setSlideNumber(slideNumber-1)
       listRef.current.style.transform = `translateX(${1475 + distance}px)`
    }
    if(direction === "right" && slideNumber < 1){ 
      setSlideNumber(slideNumber+1)
       listRef.current.style.transform = `translateX(${-1470 + distance}px)`
    }
    console.log(distance);

  }
  return (
    <div className="list">
        <span className="listTitle">Continue to watch</span>
        <div className="wrapper">
            <ArrowBackIosOutlined className="sliderArrow left" onClick={() => handleClick("left")}
            style={{display: !isMoved  && "none"}}/>
            <div className="container" ref={listRef}>
                <Listcreate index5={0}/>
                <Listcreate index5={1}/>
                <Listcreate index5={2}/>
                <Listcreate index5={3}/>
                <Listcreate index5={4}/>
                <Listcreate index5={5}/>
                <Listcreate index5={6}/>
                <Listcreate index5={7}/>
                <Listcreate index5={8}/>
                <Listcreate index5={9}/>           
            </div>
            <ArrowForwardIosOutlined className="sliderArrow right" onClick={() => handleClick("right")}/>
        </div>
    </div>
  )
}

export default List;
