import { useRef, useState } from "react"
import "./register.scss"

export default function Register() {

  const [email , setEmail] = useState("")
  const [password , setPassword] = useState("")
  const emailRef = useRef()
  const passwordRef = useRef()

  const handleStart= () => {
    setEmail(emailRef.current.value)
  }

  const handleFinish= () => {
    setEmail(emailRef.current.value)
  }
  return (
    <div className="register">
       <div className="top">
         <div className="wrapper">
           <img className="logo" src="https://upload.wikimedia.org/wikipedia/commons/0/08/Netflix_2015_logo.svg" alt="" />

           <buttton className="loginButton">Sign In</buttton>
           </div>
       </div>

       <div className="container">
           <h1>Unlimited movies, Tv shows and more.</h1>
           <h2>Watch anywhere. Cancel anytime.</h2>
           <p>
               Reday to watch? Enter your Email to creat or restart your membership.
           </p>
           {
             !email ? (
           <div className="input">
               <input type="email" placeholder="email address" ref={emailRef}/>
               <button className="registerButton" onClick={handleStart}>Get Started </button>
           </div>
             ) : (<div className="input">
             <form type="password" placeholder="password" ref={passwordRef}/>
             <button className="registerButton" onClick={handleFinish}>Start </button>
         </div>)}
       </div>
    </div>
  )
}
